import React, { useEffect, useState } from 'react';

const API_BASE = 'http://localhost:8000/api';

function CrossingAlerts() {
  const [crossings, setCrossings] = useState([]);

  useEffect(() => {
    fetchCrossings();
  }, []);

  const fetchCrossings = async () => {
    const res = await fetch(`${API_BASE}/crossings?limit=10`);
    const data = await res.json();
    setCrossings(data);
  };

  return (
    <div>
      <h2>Recent Crossings</h2>
      <ul>
        {crossings.map(crossing => (
          <li key={crossing.id}>
            {crossing.asset_id} {crossing.crossing_type} region {crossing.region_id} at {new Date(crossing.crossing_time).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CrossingAlerts;
