import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polygon } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const API_BASE = 'http://localhost:8000/api';

function MapView() {
  const [positions, setPositions] = useState([]);
  const [regions, setRegions] = useState([]);
  const [selectedAsset, setSelectedAsset] = useState(null);

  useEffect(() => {
    fetchRegions();
    fetchPositions();
  }, []);

  const fetchRegions = async () => {
    const res = await fetch(`${API_BASE}/regions`);
    const data = await res.json();
    setRegions(data);
  };

  const fetchPositions = async () => {
    const res = await fetch(`${API_BASE}/positions?limit=100`);
    const data = await res.json();
    setPositions(data.positions);
  };

  const handleMarkerClick = async (assetId) => {
    const res = await fetch(`${API_BASE}/positions/${assetId}/track`);
    const data = await res.json();
    setSelectedAsset(data);
  };

  return (
    <MapContainer center={[39.8283, -98.5795]} zoom={4} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {regions.map(region => (
        <Polygon key={region.id} positions={region.boundary.coordinates[0].map(coord => [coord[1], coord[0]])} />
      ))}
      {positions.map(pos => (
        <Marker key={pos.id} position={[pos.latitude, pos.longitude]} onClick={() => handleMarkerClick(pos.asset_id)}>
          <Popup>{pos.asset_id} - {pos.asset_type}</Popup>
        </Marker>
      ))}
      {selectedAsset && (
        <Polygon positions={selectedAsset.track.coordinates.map(coord => [coord[1], coord[0]])} color="blue" />
      )}
    </MapContainer>
  );
}

export default MapView;
